import React, { useEffect, useState } from 'react';
import _ from 'lodash';
import { Helmet } from "react-helmet";
import { Label, Table, Segment, Modal, Header, Form, TextArea, Dropdown } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'
import './App.css';


const TagDropDown = (props: { tagSet: Array<string>, onChange: any, currentTags: Array<string> }) => {
  let initOptions: Array<{ key: string, text: string, value: string }> = [];
  props.tagSet.forEach(tag => {
    initOptions.push({ key: tag, text: tag, value: tag });
  });

  const [options, setOptions] = useState(initOptions);

  const handleAddition = (e: {}, { value }: any) => {
    setOptions([{ key: value, text: value, value: value }, ...options]);
  }
  const handleChange = (e: {}, { value }: any) => { props.onChange(value); }
  return (
    <Dropdown
      options={options}
      value={props.currentTags}
      placeholder='Select Tag'
      search
      selection
      fluid
      multiple
      allowAdditions
      onAddItem={handleAddition}
      onChange={handleChange}
    />
  );
}

const TaskRow = (props: { taskInfo: [number, string, string, Array<string>, boolean], tagSet: Array<string> }) => {
  const [open, setOpen] = useState<boolean>(false);
  const [taskName, setTaskName] = useState<string>(props.taskInfo[1]);
  const [taskDescription, setTaskDescription] = useState<string>(props.taskInfo[2]);
  const [tags, setTags] = useState<Array<string>>(props.taskInfo[3]);
  const [isdone, setIsDone] = useState<boolean>(props.taskInfo[4]);
  const handleTagsChange = (tags: Array<string>) => {
    setTags(tags);
    // console.log(tags);
  }
  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={
        <Table.Row key={props.taskInfo[0]}>
          <Table.Cell>{taskName}</Table.Cell>
          <Table.Cell>{taskDescription}</Table.Cell>
          <Table.Cell>
            {tags.map((tag) =>
              <Label color="blue" tag>{tag}</Label>)
            }
          </Table.Cell>
          <Table.Cell>
            {isdone ? <Label color="green">Done</Label> : <Label color="pink">Not Done</Label>}
          </Table.Cell>
        </Table.Row>
      }
    >
      <Modal.Header>Edit Task</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Header>Editing Task: {props.taskInfo[1]}</Header>
          <Form>
            <Form.Field>
              <label>Task Name</label>
              <input placeholder="Task Name" defaultValue={props.taskInfo[1]} onChange={(e: any) => setTaskName(e.target.value)} />
            </Form.Field>
            <Form.Field>
              <label>Task Description</label>
              <TextArea placeholder='Task Description' defaultValue={props.taskInfo[2]} onChange={(e: any) => setTaskDescription(e.target.value)} />
            </Form.Field>
            <Form.Field>
              <TagDropDown tagSet={props.tagSet} currentTags={tags} onChange={handleTagsChange} />
            </Form.Field>
          </Form>

        </Modal.Description>
      </Modal.Content>
    </Modal>);
}

function sortReducer(state: any, action: any) {
  switch (action.type) {
    case 'RESET':
      return initTable(action.payload);
    case 'CHANGE_SORT':
      console.log("change sort");
      if (state.column === action.column) {
        return {
          ...state,
          data: state.data.slice().reverse(),
          direction:
            state.direction === 'ascending' ? 'descending' : 'ascending',
        }
      }

      return {
        column: action.column,
        data: _.sortBy(state.data, [action.column]),
        direction: 'ascending',
      }
    default:
      throw new Error()
  }
}

const initTable = (newdata: Array<{ index: number, name: string, desc: string, tags: Array<string>, isdone: boolean }>) => {
  console.log("initialising table");
  return { column: null, data: newdata, direction: null };
}

const ElementsTable = () => {
  const [tagSet, setTagSet] = useState<Array<string>>(new Array<string>());

  // Sorting
  const [state, dispatch] = React.useReducer(sortReducer, [
    { index: 1, name: 'Learn HTML', desc: "Create at least something", tags: ['Task 1.1', 'Task 1.2'], isdone: true },
    { index: 2, name: 'Learn CSS', desc: "Create at least a stylesheet", tags: ['Task 2.1', 'Task 2.2'], isdone: false },
    { index: 3, name: 'Learn JAVASCRIPT', desc: "Create at least an APP", tags: ['Task 3.1', 'Task 3.2'], isdone: false },
  ], initTable); // here state = {column, data, dir}
  const { column, data, direction } = state;


  useEffect(() => {
    // Task are stored as an array of (creation index, name, description, {tag1, tag2, ...}, isdone)
    let initTasks: Array<{ index: number, name: string, desc: string, tags: Array<string>, isdone: boolean }> = [
      { index: 1, name: 'Learn HTML', desc: "Create at least something", tags: ['Task 1.1', 'Task 1.2'], isdone: true },
      { index: 2, name: 'Learn CSS', desc: "Create at least a stylesheet", tags: ['Task 2.1', 'Task 2.2'], isdone: false },
      { index: 3, name: 'Learn JAVASCRIPT', desc: "Create at least an APP", tags: ['Task 3.1', 'Task 3.2'], isdone: false },
    ];
    dispatch({ type: 'RESET', payload: initTasks });

    let initTagSet = new Array<string>();
    initTasks.forEach(task => {
      task.tags.forEach(tag => {
        initTagSet.push(tag);
      });
    });

    setTagSet(initTagSet);
  }, []);

  useEffect(() => {
    console.log("table data", data);
  });
  return (
    <Table celled selectable sortable fixed>
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell
            sorted={column === 'name' ? direction : null}
            onClick={() => {
              dispatch({ type: 'CHANGE_SORT', column: 'name' });
            }}
            key="name"
          >Task Name</Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'desc' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'desc' })}
            key="desc"
          >Task Description</Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'tags' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'tags' })}
            key="tags"
          >Tags</Table.HeaderCell>
          <Table.HeaderCell
            sorted={column === 'isdone' ? direction : null}
            onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'isdone' })}
            key="isdone"
          >Completed</Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {data.map(({ index, name, desc, tags, isdone }: { index: number, name: string, desc: string, tags: Array<string>, isdone: boolean }) =>
          //<TaskRow taskInfo={[index, name, desc, tags, isdone]} tagSet={tagSet}></TaskRow>
          <Table.Row>
            <Table.Cell>{name}</Table.Cell>
            <Table.Cell>{desc}</Table.Cell>
            <Table.Cell>{tags.join(', ')}</Table.Cell>
            <Table.Cell>{isdone ? <Label color="green">Done</Label> : <Label color="pink">Not Done</Label>}</Table.Cell>
          </Table.Row>
        )}

        <Table.Row>
          <Table.Cell>
            {data[0].name}
          </Table.Cell>
        </Table.Row>
      </Table.Body>
    </Table>
  );
}

const MainBodySegment = () => {
  return (
    <div className="mainBodySegment">
      <Segment>
        <div style={{ textAlign: 'center' }}>
          <h1>Brandon's "Modern" To-Do App</h1>
        </div>

        <ElementsTable />
      </Segment >
    </div >
  );
