import React, { useEffect } from 'react';
import { Helmet } from "react-helmet";
import { Header, Table, Rating, Segment, Button } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'
import './App.css';

const ElementsTable = () => {
  var tasks: Array<[number, string, Array<string>, Boolean]> = []; // [id, name, tags, isDone]
  useEffect(() => {
    // Task are stored as an array of (creation index, name, {tag1, tag2, ...}, isdone)
    tasks = [
      [1, 'Task 1', ['Task 1.1', 'Task 1.2'], false],
      [2, 'Task 2', ['Task 2.1', 'Task 2.2'], false],
      [3, 'Task 3', ['Task 3.1', 'Task 3.2'], false],
    ];

  });
  return (
    <Table celled>
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell>Task Name</Table.HeaderCell>
          <Table.HeaderCell>Tags</Table.HeaderCell>
          <Table.HeaderCell>Completed</Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {tasks.map(([index, name, tags, isdone]) => {
          return (
            <Table.Row key={index}>
              <Table.Cell>{name}</Table.Cell>
              <Table.Cell>
                {tags.map((tag) => {
                  return <Button key={tag}>{tag}</Button>
                })}
              </Table.Cell>
              <Table.Cell>
                <Rating icon='star' defaultRating={3} maxRating={5} />
              </Table.Cell>
            </Table.Row>
          );
        })}
      </Table.Body>
    </Table>
  );
}

const MainBodySegment = () => {
  return (
    <div className="mainBodySegment">
      <Segment>
        <div style = {{textAlign: 'center'}}>
          <h1>Brandon's "Modern" To-Do App</h1>
        </div>

        <ElementsTable />
      </Segment >
    </div >
  );
}

function App() {
  useEffect(() => {
    document.title = "Modern TODO App";

  });

  return (
    <div
      style={{
        padding: '3em',
      }} >
      <Helmet>
        <style>{"body { background-color: #eddefa; }"}</style>
      </Helmet>
      <MainBodySegment />
    </div >
  );
}

export default App;
