import React, { useEffect, useState, Component } from 'react';
import { Helmet } from "react-helmet";
import { Label, Table, Segment, Button, Modal, Header, Form, TextArea, Dropdown } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'
import './App.css';


const TagDropDown = (props: { tagSet: Array<string> }) => {
  let initOptions: Array<{ key: string, text: string, value: string }> = [];
  props.tagSet.forEach(tag => {
    initOptions.push({ key: tag, text: tag, value: tag });
  });

  const [options, setOptions] = useState(initOptions);

  const handleAddition = (e: Event, v:{value:string}) => {
      setOptions([{key:v.value, text: v.value, value:v.value }, ...options]);
  }


/*
handleChange = (e, { value }) => this.setState({ currentValues: value })
*/
return (
  <Dropdown
    options={options}
    placeholder='Choose Languages'
    search
    selection
    fluid
    multiple
    allowAdditions
    onAddItem={handleAddition}
  />
);
}

const EditTaskModal = (props: { taskInfo: [number, string, string, Array<string>, Boolean], tagSet: Array<string> }) => {
  const [open, setOpen] = useState<boolean>(false);
  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Show Modal</Button>}
    >
      <Modal.Header>Edit Task</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Header>Editing Task: {props.taskInfo[1]}</Header>
          <Form>
            <Form.Field>
              <label>Task Name</label>
              <input placeholder="Task Name" defaultValue={props.taskInfo[1]} />
            </Form.Field>
            <Form.Field>
              <label>Task Description</label>
              <TextArea placeholder='Task Description' defaultValue={props.taskInfo[2]} />
            </Form.Field>
            <Form.Field>
              <TagDropDown tagSet={props.tagSet} />

            </Form.Field>

          </Form>
        </Modal.Description>
      </Modal.Content>
    </Modal>);
}


const ElementsTable = () => {
  const [tasks, setTasks] = useState<Array<[number, string, string, Array<string>, Boolean]>>([]);
  const [tagSet, setTagSet] = useState<Array<string>>(new Array<string>());
  useEffect(() => {
    // Task are stored as an array of (creation index, name, description, {tag1, tag2, ...}, isdone)
    setTasks([
      [1, 'Learn HTML', "Create at least something", ['Task 1.1', 'Task 1.2'], false],
      [2, 'Task 2', "", ['Task 2.1', 'Task 2.2'], false],
      [3, 'Task 3', "", ['Task 3.1', 'Task 3.2'], false],
    ]);

    console.log(tasks);

    let tagSet = new Array<string>();
    tasks.forEach(task => {
      task[3].forEach(tag => {
        tagSet.push(tag);
      });
    });

    setTagSet(tagSet);
    console.log(tagSet);
  });
  return (
    <Table celled>
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell>Task Name</Table.HeaderCell>
          <Table.HeaderCell>Tags</Table.HeaderCell>
          <Table.HeaderCell>Completed</Table.HeaderCell>
          <Table.HeaderCell>Edit Task</Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {tasks.map(([index, name, desc, tags, isdone]) =>
          <Table.Row key={index}>
            <Table.Cell>{name}</Table.Cell>
            <Table.Cell>
              {tags.map((tag) =>
                <Label color="blue" tag>{tag}</Label>)
              }
            </Table.Cell>
            <Table.Cell>
              {isdone ? <Label color="green">Done</Label> : <Label color="pink">Not Done</Label>}
            </Table.Cell>
            <Table.Cell>
              <EditTaskModal taskInfo={[index, name, desc, tags, isdone]} tagSet={tagSet}></EditTaskModal>
            </Table.Cell>
          </Table.Row>
        )}
      </Table.Body>
    </Table>
  );
}

const MainBodySegment = () => {
  return (
    <div className="mainBodySegment">
      <Segment>
        <div style={{ textAlign: 'center' }}>
          <h1>Brandon's "Modern" To-Do App</h1>
        </div>

        <ElementsTable />
      </Segment >
    </div >
  );
}

function App() {
  useEffect(() => {
    document.title = "Modern TODO App";

  });

  return (
    <div
      style={{
        padding: '3em',
      }} >
      <Helmet>
        <style>{"body { background-color: #eddefa; }"}</style>
      </Helmet>
      <MainBodySegment />
    </div >
  );
}

export default App;
