import React, { useEffect } from 'react';
import { Helmet } from "react-helmet";
import { Header, Table, Rating, Segment, Button } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'
import './App.css';

const ElementsTable = () => {
  const [tasks: Array<number>, setTasks] = React.useState([]);
  useEffect(() => {
    // Task are stored as an array of (creation index, name, {tag1, tag2, ...}, isdone)
    // alert("Hello");
    tasks = [
      [1, 'Task 1', ['Task 1.1', 'Task 1.2'], false],
      [2, 'Task 2', ['Task 2.1', 'Task 2.2'], false],
      [3, 'Task 3', ['Task 3.1', 'Task 3.2'], false],
    ];

    console.log(tasks);

    rows = tasks.map(([index, name, tags, isdone]) =>
      <Table.Row>
        <Table.Cell>{name}</Table.Cell>
        <Table.Cell>
          Helllo
        </Table.Cell>
        <Table.Cell>
          Done
        </Table.Cell>
      </Table.Row>
    );

    console.log(rows);

  });
  return (
    <Table celled>
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell>Task Name</Table.HeaderCell>
          <Table.HeaderCell>Tags</Table.HeaderCell>
          <Table.HeaderCell>Completed</Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        <Table.Row>
          <Table.Cell>Example Task 1</Table.Cell>
          <Table.Cell> hello</Table.Cell>
          <Table.Cell>bye</Table.Cell>
        </Table.Row>
        {rows}
      </Table.Body>
    </Table>
  );
}

const MainBodySegment = () => {
  return (
    <div className="mainBodySegment">
      <Segment>
        <div style={{ textAlign: 'center' }}>
          <h1>Brandon's "Modern" To-Do App</h1>
        </div>

        <ElementsTable />
      </Segment >
    </div >
  );
}

function App() {
  useEffect(() => {
    document.title = "Modern TODO App";

  });

  return (
    <div
      style={{
        padding: '3em',
      }} >
      <Helmet>
        <style>{"body { background-color: #eddefa; }"}</style>
      </Helmet>
      <MainBodySegment />
    </div >
  );
}

export default App;
