import React, { useEffect, useState } from 'react';
import { Helmet } from "react-helmet";
import { Label, Table, Segment, Button, Modal, Header, Form } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'
import './App.css';

const EditTaskModal = (props:{taskInfo: [number, string, Array<string>, Boolean]}) => {
  const [open, setOpen] = useState<boolean>(false);
  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Show Modal</Button>}
    >
      <Modal.Header>Edit Task</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Header>Editing Task {props.taskInfo[1]}</Header>
          <Form>
            <Form.Field>
              <label>Task Name</label>
              <input placeholder="Task Name" />
            </Form.Field>
            <Form.Field>
              <label>Task Description</label>
              <input placeholder='Task Description' />
            </Form.Field>

            </Form>
        </Modal.Description>
      </Modal.Content>
    </Modal>);
}


const ElementsTable = () => {
  const [tasks, setTasks] = useState<Array<[number, string, Array<string>, Boolean]>>([]);
  useEffect(() => {
    // Task are stored as an array of (creation index, name, {tag1, tag2, ...}, isdone)
    setTasks([
      [1, 'Learn HTML', ['Task 1.1', 'Task 1.2'], false],
      [2, 'Task 2', ['Task 2.1', 'Task 2.2'], false],
      [3, 'Task 3', ['Task 3.1', 'Task 3.2'], false],
    ]);

    console.log(tasks);

  });
  return (
    <Table celled>
      <Table.Header>
        <Table.Row>
          <Table.HeaderCell>Task Name</Table.HeaderCell>
          <Table.HeaderCell>Tags</Table.HeaderCell>
          <Table.HeaderCell>Completed</Table.HeaderCell>
          <Table.HeaderCell>Edit Task</Table.HeaderCell>
        </Table.Row>
      </Table.Header>

      <Table.Body>
        {tasks.map(([index, name, tags, isdone]) =>
          <Table.Row key={index}>
            <Table.Cell>{name}</Table.Cell>
            <Table.Cell>
              {tags.map((tag) =>
                <Label color="blue" tag>{tag}</Label>)
              }
            </Table.Cell>
            <Table.Cell>
              {isdone ? <Label color="green">Done</Label> : <Label color="pink">Not Done</Label>}
            </Table.Cell>
            <Table.Cell>
              <EditTaskModal taskInfo={[index,name, tags, isdone]}></EditTaskModal>
            </Table.Cell>
          </Table.Row>
        )}
      </Table.Body>
    </Table>
  );
}

const MainBodySegment = () => {
  return (
    <div className="mainBodySegment">
      <Segment>
        <div style={{ textAlign: 'center' }}>
          <h1>Brandon's "Modern" To-Do App</h1>
        </div>

        <ElementsTable />
      </Segment >
    </div >
  );
}

function App() {
  useEffect(() => {
    document.title = "Modern TODO App";

  });

  return (
    <div
      style={{
        padding: '3em',
      }} >
      <Helmet>
        <style>{"body { background-color: #eddefa; }"}</style>
      </Helmet>
      <MainBodySegment />
    </div >
  );
}

export default App;
