const initialState = {
  loading: false,
  results: [],
  value: '',
}

function exampleReducer(state: any, action: any) {
  switch (action.type) {
    case 'CLEAN_QUERY':
      return initialState
    case 'START_SEARCH':
      console.log("seaching")
      return { ...state, loading: true, value: action.query }
    case 'FINISH_SEARCH':
      return { ...state, loading: false, results: action.results }
    case 'UPDATE_SELECTION':
      return { ...state, value: action.selection }

    default:
      throw new Error()
  }
}

function SearchBox(props: { source: Array<{ index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }> }) {
  const [state, dispatch] = React.useReducer(exampleReducer, initialState)
  const { loading, results, value } = state

  const timeoutRef = React.useRef(0)
  const handleSearchChange = React.useCallback((e: any, data: any) => {
    clearTimeout(timeoutRef.current)
    dispatch({ type: 'START_SEARCH', query: data.value })

    timeoutRef.current = window.setTimeout(() => {
      if (data.value.length === 0) {
        dispatch({ type: 'CLEAN_QUERY' })
        return
      }

      const re = new RegExp(_.escapeRegExp(data.value), 'i')
      const isMatch = (result: any) => re.test(result.title)

      dispatch({
        type: 'FINISH_SEARCH',
        results: _.filter(props.source, isMatch),
      })
    }, 300)
  }, [])
  React.useEffect(() => {
    console.log("search box", props.source);
    return () => {
      clearTimeout(timeoutRef.current)
    }
  }, [])


  return (
    <Search
      loading={loading}
      placeholder='Search...'
      onResultSelect={(e, data) =>
        dispatch({ type: 'UPDATE_SELECTION', selection: data.result.title })
      }
      onSearchChange={handleSearchChange}
      results={results}
      value={value}
    />

  )
}


const TaskTable = () => {
  const [tagSet, setTagSet] = useState<Array<string>>(new Array<string>());

  // Sorting
  function sortReducer(state: any, action: any) {
    switch (action.type) {
      case 'RESET':
        return initTable(action.payload);
      case 'CHANGE_SORT':
        if (state.column === action.column) {
          return {
            ...state,
            data: state.data.slice().reverse(),
            direction:
              state.direction === 'ascending' ? 'descending' : 'ascending',
          }
        }

        return {
          column: action.column,
          data: _.sortBy(state.data, [action.column]),
          direction: 'ascending',
        }
      default:
        throw new Error()
    }
  }
  const initTable = (newdata: Array<{ index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }>) => {
    return { column: null, data: newdata, direction: null };
  }
  const [state, dispatch] = React.useReducer(sortReducer, [], initTable); // here state = {column, data, dir}
  const { column, data, direction } = state;

  // Searching
  const [searchData, setSearchData] = useState<Array<{ index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }>>(new Array<{ index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }>());
  const [searchTagOptions, setSearchTagOptions] = useState<Array<{ key: string, text: string, value: string }>>([]);
  const [searchField, setSearchField] = useState<string>("");
  const [searchInDesc, setSearchInDesc] = useState<boolean>(false);
  const [searchTags, setSearchTags] = useState<Array<string>>([]);
  const [searchStatus, setSearchStatus] = useState<string>("");

  useEffect(() => {
    // Task are stored as an array of (creation index, name, description, {tag1, tag2, ...}, taskStatus)
    let initTasks: Array<{ index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }> = [
      { index: 1, name: 'Learn HTML', desc: "Create at least something", tags: ['Task 1.1', 'Task 1.2', "Brandon", "Hello", "hello"], taskStatus: "Not Started" },
      { index: 2, name: 'Learn CSS', desc: "Create at least a stylesheet", tags: ['Task 2.1', 'Task 2.2'], taskStatus: "In Progress" },
      { index: 3, name: 'Learn JAVASCRIPT', desc: "Create at least an APP", tags: ['Task 3.1', 'Task 3.2'], taskStatus: "Completed" },
    ];
    dispatch({ type: 'RESET', payload: initTasks });
    setSearchData(initTasks);

    let initTagSet = new Array<string>();
    let initsearchTagOptions = new Array<{ key: string, text: string, value: string }>();
    initTasks.forEach(task => {
      task.tags.forEach(tag => {
        initTagSet.push(tag);
        initsearchTagOptions.push({ key: tag, text: tag, value: tag });
      });
    });

    // Sort Tags
    initTagSet.sort();
    initsearchTagOptions.sort((a, b) => {
      if (a.text.toLowerCase() < b.text.toLowerCase()) { return -1; }
      if (a.text.toLowerCase() > b.text.toLowerCase()) { return 1; }
      return 0;
    });
    // remove duplicates
    initTagSet = Array.from(new Set(initTagSet));
    initsearchTagOptions = Array.from(new Set(initsearchTagOptions));

    setSearchTagOptions(initsearchTagOptions);
    setTagSet(initTagSet);
  }, []);

  /*
  useEffect(() => {
    console.log(tagSet)
  }, [tagSet]);
  useEffect(() => {
    console.log(data)
  }, [data]);*/
  return (
    <div>
      <Segment>

        <Form>
          <Form.Group inline>
            <label>Search</label>
            <SearchBox key={JSON.stringify(searchData)} source={searchData} />
            <div>&nbsp;&nbsp;</div>
            <div style={{ display: "flex", alignItems: "center" }}>
              <Form.Field  >
                <Checkbox label='Search in Descriptions' />
              </Form.Field>
            </div>

            <label>Filters</label>
            <Dropdown
              placeholder='Select Tags'
              fluid
              multiple
              search
              selection
              options={searchTagOptions}
            />
            <div>&nbsp;&nbsp;</div>
            <Dropdown
              placeholder='Select Status'
              clearable
              selection
              options={statuses}
            />
            <div>&nbsp;&nbsp;</div>
            <Form.Button content='Submit' />
          </Form.Group>
        </Form>
      </Segment>
      <Table celled selectable sortable fixed>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell
              sorted={column === 'name' ? direction : null}
              onClick={() => {
                dispatch({ type: 'CHANGE_SORT', column: 'name' });
              }}
              key="name"
            >Task Name</Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'desc' ? direction : null}
              onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'desc' })}
              key="desc"
            >Task Description</Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'tags' ? direction : null}
              onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'tags' })}
              key="tags"
            >Tags</Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'taskStatus' ? direction : null}
              onClick={() => dispatch({ type: 'CHANGE_SORT', column: 'taskStatus' })}
              key="taskStatus"
            >Completed</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          {data.map((rowData: { index: number, name: string, desc: string, tags: Array<string>, taskStatus: string }) =>
            <TaskRow key={rowData.index} taskInfo={rowData} tagSet={tagSet}></TaskRow>
